(65, 1, '2013-11-28 12:17:43', '2013-11-28 12:17:43', '<!DOCTYPE html>\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n	<head>\r\n		<title>Way2Web</title>	\r\n		<link rel="stylesheet" type="text/css" href="css/style.css">\r\n		<script src="http://code.jquery.com/jquery-1.7.2.min.js"></script>\r\n		<script src="js/global.js"></script>\r\n	</head>\r\n	\r\n	<body>\r\n		<div class="wrapper">\r\n			<div class="container">\r\n				<div class="inner_container">\r\n					<header>\r\n						<div class="header_container">\r\n							<div class="left_header">\r\n								<div class="logo">\r\n									<img src="img/logo.png" alt="">\r\n								</div>\r\n								<div class="logo_name">\r\n									WAY2<span>WEB</span>\r\n								</div>\r\n								<br class="clear">\r\n							</div>\r\n							<nav>\r\n								<div class="menu"><a href="#" >DESIGN</a></div>\r\n								<div class="menu"><a href="#" >DEVELOPMENT</a></div>\r\n								<div class="menu"><a href="#" >MARKETING</a></div>\r\n								<div class="menu"><a href="#" >PORTFOLIO</a></div>\r\n								<div class="menu"><a href="#" >CONTACT</a></div>\r\n								<br class="clear">\r\n							</nav>\r\n							<div class="right_header">\r\n								<a href="#"> <img src="img/facebook.png" alt=""></a>\r\n							</div>\r\n							<br class="clear">\r\n						</div>\r\n					</header><!-- header ends here -->\r\n				\r\n					<section>\r\n						<div class="section_container">\r\n							<div class="top_section">\r\n								<div class="left_content">\r\n									<p>\r\n										FFN DIGITALE AGENCY DIF\r\n										GAAT VOR FFN\r\n										VOLLEDIGE OPLOSSING\r\n									</p>\r\n								</div>\r\n								<div class="right_content">\r\n									<div class="left_arrow">\r\n										<a href="#"><img src="img/left_arrow.png" alt=""></a>\r\n									</div>\r\n									<div class="slider_img">						\r\n										<img src="img/slider_img.png" alt="">						\r\n									</div>\r\n									<div class="right_arrow">\r\n										<a href="#"><img src="img/right_arrow.png" alt=""></a>\r\n									</div>\r\n									<br class="clear">\r\n								</div>\r\n								<br class="clear">\r\n							</div>\r\n							\r\n							<div class="bottom_section">						\r\n								<div class="section_tabs">\r\n									<a href=""><img src="img/design.png" alt=""></a>\r\n								</div>\r\n								<div class="section_tabs">\r\n									<a href=""><img src="img/development.png" alt=""></a>\r\n								</div>\r\n								<div class="section_tabs">\r\n									<a href=""><img src="img/marketing.png" alt=""></a>\r\n								</div>\r\n								<br class="clear">					\r\n							</div>		\r\n						</div>\r\n					</section>	<!-- Section ends here -->		\r\n				</div><!-- Inner_Container class ends here -->\r\n			</div><!-- Container class ends here -->\r\n			<div class="push"></div>\r\n		</div><!-- Wrapper class ends here -->\r\n		<footer>\r\n			<div class="footer_logo">\r\n			</div>\r\n		</footer><!-- footer ends here -->\r\n	</body>\r\n</html>', 'CONTACT', '', 'inherit', 'open', 'open', '', '52-revision-v1', '', '', '2013-11-28 12:17:43', '2013-11-28 12:17:43', '', 52, 'http://localhost/wordpress/?p=65', 0, 'revision', '', 0),
